import{_ as l,c as a,a as e,b as t,t as r,d as n,o as s}from"./app.625f3316.js";const o="/assets/image-20220526103323766.124b6400.png",d="/assets/image-20220526103725228.21d4b03b.png",h="/assets/image-20220526103756703.6fe43f58.png",_="/assets/image-20220526104322317.15131979.png",p="/assets/image-20220526104327056.f7791e8b.png",m="/assets/image-20220526104627398.fc23a762.png",c="/assets/image-20220526104815162.b6c5a3fc.png",g="/assets/image-20220526104859949.34f32d81.png",u="/assets/image-20220526104937150.4dc4b49d.png",v="/assets/image-20220526105003254.23fd1515.png",M="/assets/image-20220526105049142.c7bb14ba.png",N="/assets/image-20220526105107232.bc0ef3f5.png",k="/assets/image-20220526105135032.b121710e.png",C=JSON.parse('{"title":"网络管理","description":"","frontmatter":{"title":"网络管理"},"headers":[{"level":2,"title":"1 网络管理基础","slug":"_1-网络管理基础","link":"#_1-网络管理基础","children":[]},{"level":2,"title":"2 SNMNP","slug":"_2-snmnp","link":"#_2-snmnp","children":[{"level":3,"title":"2.1 网络管理协议五大标准","slug":"_2-1-网络管理协议五大标准","link":"#_2-1-网络管理协议五大标准","children":[]},{"level":3,"title":"2.2 SNMPv1","slug":"_2-2-snmpv1","link":"#_2-2-snmpv1","children":[]},{"level":3,"title":"2.3 SNMP协议的操作","slug":"_2-3-snmp协议的操作","link":"#_2-3-snmp协议的操作","children":[]},{"level":3,"title":"2.4 SNMPv1 机制与问题","slug":"_2-4-snmpv1-机制与问题","link":"#_2-4-snmpv1-机制与问题","children":[]},{"level":3,"title":"2.5 SNMPv2","slug":"_2-5-snmpv2","link":"#_2-5-snmpv2","children":[]},{"level":3,"title":"2.6 SNMPv3","slug":"_2-6-snmpv3","link":"#_2-6-snmpv3","children":[]},{"level":3,"title":"2.7 管理数据库 MIB-2","slug":"_2-7-管理数据库-mib-2","link":"#_2-7-管理数据库-mib-2","children":[]}]},{"level":2,"title":"3 RMON","slug":"_3-rmon","link":"#_3-rmon","children":[]},{"level":2,"title":"4 网络管理命令","slug":"_4-网络管理命令","link":"#_4-网络管理命令","children":[{"level":3,"title":"4.1 Ping","slug":"_4-1-ping","link":"#_4-1-ping","children":[]},{"level":3,"title":"4.2 故障诊断 ping traceroute","slug":"_4-2-故障诊断-ping-traceroute","link":"#_4-2-故障诊断-ping-traceroute","children":[]},{"level":3,"title":"4.3 ARP","slug":"_4-3-arp","link":"#_4-3-arp","children":[]},{"level":3,"title":"4.4 netstat","slug":"_4-4-netstat","link":"#_4-4-netstat","children":[]}]},{"level":2,"title":"4.5 route nslookup","slug":"_4-5-route-nslookup","link":"#_4-5-route-nslookup","children":[]},{"level":2,"title":"5 章节总结","slug":"_5-章节总结","link":"#_5-章节总结","children":[]}],"relativePath":"articles/02-运维/网络工程师/12-网络管理.md","lastUpdated":null}'),P={name:"articles/02-运维/网络工程师/12-网络管理.md"},f={id:"frontmatter-title",tabindex:"-1"},S=e("a",{class:"header-anchor",href:"#frontmatter-title","aria-hidden":"true"},"#",-1),b=n('<h2 id="_1-网络管理基础" tabindex="-1">1 网络管理基础 <a class="header-anchor" href="#_1-网络管理基础" aria-hidden="true">#</a></h2><ul><li>网络管理软件 vs 网络监控软件</li><li>网络管理五大功能域∶故障管理、配置管理、计费管理、性能管理和安全管理 <ul><li>助记:“安配能计障”</li></ul></li><li>故障管理：尽快发现故障，找出故障原因，以便采取补救措施(15上48)</li></ul><p><strong>网络监控系统体系结构</strong></p><p><img src="'+o+'" alt="image-20220526103323766"></p><p>代理与监视器两种通信方式：<mark>轮询和事件报告</mark></p><h2 id="_2-snmnp" tabindex="-1">2 SNMNP <a class="header-anchor" href="#_2-snmnp" aria-hidden="true">#</a></h2><h3 id="_2-1-网络管理协议五大标准" tabindex="-1">2.1 网络管理协议五大标准 <a class="header-anchor" href="#_2-1-网络管理协议五大标准" aria-hidden="true">#</a></h3><ol><li>ISO制定：CMIS/CMIP公共管理信息服务规范</li><li>基于TCP/IP：简单网络管理协议==<strong>SNMPv1、SNMPv2、SNMPv3</strong>==</li><li>基于局域网：远程监控网络RMON: RMON-1和RMON-2</li><li>IEEE制定：基于物理层和数据链路层CMOL</li><li>ITU-T：电信网络管理标准TMN</li></ol><h3 id="_2-2-snmpv1" tabindex="-1">2.2 SNMPv1 <a class="header-anchor" href="#_2-2-snmpv1" aria-hidden="true">#</a></h3><ul><li>SNMP为<strong>应用层</strong>协议，通过<mark>UDP</mark>承载，端口161</li><li>不可靠，但<mark>效率高</mark>，网络管理不会太多增加网络负载</li><li>每个代理进程管理若干被管理对象，并且与某些管理站建立==团体( community )==关系 <ul><li>相当于简单的认证方式</li></ul></li></ul><p><img src="'+d+'" alt="image-20220526103725228"></p><h3 id="_2-3-snmp协议的操作" tabindex="-1">2.3 SNMP协议的操作 <a class="header-anchor" href="#_2-3-snmp协议的操作" aria-hidden="true">#</a></h3><p><img src="'+h+'" alt="image-20220526103756703"></p><ul><li>为了简化书写，前三个可以简写为：get、get-next和set。</li><li>SNMP双端口：客户端用端口<strong>161</strong>来接收 get/set，服务器端用端口<strong>162</strong>来接收 trap</li></ul><h3 id="_2-4-snmpv1-机制与问题" tabindex="-1">2.4 SNMPv1 机制与问题 <a class="header-anchor" href="#_2-4-snmpv1-机制与问题" aria-hidden="true">#</a></h3><ul><li>SNMP网络管理中，管理站和代理站之间可以是<mark>一对多</mark>关系，也可以是<mark>多对一</mark>关系</li><li>RFC1157 规定 SNMP基本认知和控制机制，通过团体名验证实现</li><li>团体名 Community 明文传输，不安全</li></ul><h3 id="_2-5-snmpv2" tabindex="-1">2.5 SNMPv2 <a class="header-anchor" href="#_2-5-snmpv2" aria-hidden="true">#</a></h3><ul><li>SNMPv2增加定义了 GetBulk 和 inform 两个新协议操作</li><li>GetBulk：快速获取大块数据</li><li>Inform：允许一个NMS向另一个NMS发送Trap信息/接收响应消息</li></ul><p><img src="'+_+'" alt="image-20220526104322317"></p><p><img src="'+p+'" alt="image-20220526104327056"></p><h3 id="_2-6-snmpv3" tabindex="-1">2.6 SNMPv3 <a class="header-anchor" href="#_2-6-snmpv3" aria-hidden="true">#</a></h3><ul><li>SNMPv3重新定义了==<strong>网络管理框架和安全机制</strong>==。</li><li>重新定义网络管理框架:将前两版中的管理站和代理统一叫做SNMP实体（entity）</li><li>安全机制：认证和加密传输 <ul><li>时间序列模块，提供重放攻击防护</li><li>认证模块：完整性和数据源认证，使用 SHA 或 MD5</li><li>加密模块：防止内容泄露，使用 DES 算法</li></ul></li><li>有两种威胁是SNMPv3没有防护的：<mark><strong>拒绝服务和通信分析</strong></mark></li></ul><h3 id="_2-7-管理数据库-mib-2" tabindex="-1">2.7 管理数据库 MIB-2 <a class="header-anchor" href="#_2-7-管理数据库-mib-2" aria-hidden="true">#</a></h3><ul><li>被管理对象标识符 OID</li></ul><p><img src="'+m+'" alt="image-20220526104627398"></p><h2 id="_3-rmon" tabindex="-1">3 RMON <a class="header-anchor" href="#_3-rmon" aria-hidden="true">#</a></h2><ul><li><p>RMON ( Remote Network Monitoring )</p></li><li><p>用于监视<mark>网络通信情况</mark>的设备叫网络监视器（Monitor）或网络分析器（Analyzer）、探测器（Probe）等</p><p>【监测流量vs 监测设备】</p></li><li><p>RMON定义了管理信息库 RMON MIB-II（流量信息），与 SNMP MIB（设备信息)</p></li><li><p>RMON目标：监视子网范围内通信，从而减少管理站和被管理系统之间的通信负载</p></li></ul><p><img src="'+c+'" alt="image-20220526104815162"></p><h2 id="_4-网络管理命令" tabindex="-1">4 网络管理命令 <a class="header-anchor" href="#_4-网络管理命令" aria-hidden="true">#</a></h2><h3 id="_4-1-ping" tabindex="-1">4.1 Ping <a class="header-anchor" href="#_4-1-ping" aria-hidden="true">#</a></h3><p><img src="'+g+'" alt="image-20220526104859949"></p><p><img src="'+u+'" alt="image-20220526104937150"></p><h3 id="_4-2-故障诊断-ping-traceroute" tabindex="-1">4.2 故障诊断 ping traceroute <a class="header-anchor" href="#_4-2-故障诊断-ping-traceroute" aria-hidden="true">#</a></h3><img src="'+v+'" alt="image-20220526105003254" style="zoom:25%;"><h3 id="_4-3-arp" tabindex="-1">4.3 ARP <a class="header-anchor" href="#_4-3-arp" aria-hidden="true">#</a></h3><p><img src="'+M+'" alt="image-20220526105049142"></p><h3 id="_4-4-netstat" tabindex="-1">4.4 netstat <a class="header-anchor" href="#_4-4-netstat" aria-hidden="true">#</a></h3><p><img src="'+N+'" alt="image-20220526105107232"></p><h2 id="_4-5-route-nslookup" tabindex="-1">4.5 route nslookup <a class="header-anchor" href="#_4-5-route-nslookup" aria-hidden="true">#</a></h2><p><img src="'+k+'" alt="image-20220526105135032"></p><h2 id="_5-章节总结" tabindex="-1">5 章节总结 <a class="header-anchor" href="#_5-章节总结" aria-hidden="true">#</a></h2><ul><li>网络管理五大功能域</li><li>SNMP <ul><li>SNMPv1：应用层协议、UDP、团体名</li><li>SNMPv2：getbluck、inform</li><li>SNMPv3：架构、安全</li></ul></li><li>MIB ( OID ) RMONE</li><li>网络管理命令（重点)</li></ul>',42);function x(i,I,O,R,T,B){return s(),a("div",null,[e("h1",f,[t(r(i.$frontmatter.title)+" ",1),S]),b])}const A=l(P,[["render",x]]);export{C as __pageData,A as default};
